list-view-config.query-select:
    
        PREFIX afn:   <http://jena.hpl.hp.com/ARQ/function#>
        PREFIX bibo:  <http://purl.org/ontology/bibo/>
        PREFIX vivo:  <http://vivoweb.org/ontology/core#>
        PREFIX owl:   <http://www.w3.org/2002/07/owl#>
        PREFIX rdfs:  <http://www.w3.org/2000/01/rdf-schema#>
        PREFIX vitro: <http://vitro.mannlib.cornell.edu/ns/vitro/0.7#>
        
        SELECT DISTINCT ?subclass
                        ?role ?roleLabel                         
                        ?project ?projectName ?projectLabel
                        ?dateTime
                        
            WHERE {
                        
                ?subject ?property ?role  
                
                OPTIONAL { ?role rdfs:label ?roleLabel }
                
                OPTIONAL {  ?role vivo:roleRealizedIn ?project
                            LET (?projectName := afn:localname(?project)) 
                        
                            OPTIONAL { ?project rdfs:label ?projectLabel }
                        
                            OPTIONAL { ?series a vivo:EventSeries .
                                       OPTIONAL { ?series rdfs:label ?seriesLabel }
                                       
                                       { 
                                           ?project vivo:inEventWithin ?series
                                       }  UNION {                                                                                                              
                                           ?series vivo:seriesForEvent ?project 
                                       }
                            }

                            
                            ?project vitro:mostSpecificType ?subclass .
                            
                            { 
                                ?subclass rdfs:subClassOf vivo:Project 
                            } UNION {
                                ?subclass  owl:equivalentClass vivo:Project 
                            }
                            
                           
                }   
        
                OPTIONAL { ?role vivo:dateTimeInterval ?dateTimeInterval 
                           OPTIONAL { ?dateTimeInterval vivo:start ?dateTimeStartValue .
                                      ?dateTimeStartValue vivo:dateTime ?dateTime 
                           }
                }  
                
                
                FILTER ( bound(?project) )
                
        
            } ORDER BY ?subclass DESC(?dateTime) ?projectLabel ?projectName

list-view-config.query-construct:

       PREFIX vivo: <http://vivoweb.org/ontology/core#> 
       PREFIX owl:   <http://www.w3.org/2002/07/owl#>
       PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>          
       PREFIX vitro: <http://vitro.mannlib.cornell.edu/ns/vitro/0.7#>
       
       CONSTRUCT {
           ?subject ?property ?role .
           ?role vivo:roleRealizedIn ?project .
           ?project vitro:mostSpecificType ?subclass .
           ?subclass rdfs:subClassOf vivo:Project .
           ?subclass owl:equivalentClass vivo:Project .
       } WHERE {
           { 
               ?subject ?property ?role .
               ?role vivo:roleRealizedIn ?project .
               ?project vitro:mostSpecificType ?subclass .
               ?subclass rdfs:subClassOf vivo:Project 
           } UNION {
               ?subject ?property ?role .
               ?role vivo:roleRealizedIn ?project .
               ?project vitro:mostSpecificType ?subclass .
               ?subclass owl:equivalentClass vivo:Project 
           }   
       }

PREFIX vivo: <http://vivoweb.org/ontology/core#>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>    
CONSTRUCT { 
    ?subject ?property ?role .
    ?role ?roleProperty ?roleValue . 
    ?project rdfs:label ?projectLabel 
} WHERE {
    {
        ?subject ?property ?role 
    } UNION {
        ?subject ?property ?role .
        ?role ?roleProperty ?roleValue 
    } UNION {
        ?subject ?property ?role .
        ?role vivo:roleRealizedIn ?project . 
        ?project rdfs:label ?projectLabel 
    }
}

PREFIX vivo: <http://vivoweb.org/ontology/core#>   
CONSTRUCT { 
    ?subject ?property ?role .  
    ?role vivo:dateTimeInterval ?dateTimeInterval .
    ?dateTimeInterval vivo:start ?dateTimeStartValue .
    ?dateTimeStartValue vivo:dateTime ?dateTimeStart 
} WHERE {
    ?subject ?property ?role .  
    ?role vivo:dateTimeInterval ?dateTimeInterval .
    ?dateTimeInterval vivo:start ?dateTimeStartValue .
    ?dateTimeStartValue vivo:dateTime ?dateTimeStart 
}
