list-view-config.query-select:

PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>    
PREFIX core: <http://vivoweb.org/ontology/core#>
PREFIX afn:  <http://jena.hpl.hp.com/ARQ/function#>

SELECT DISTINCT ?recordCreated 
                (afn:localname(?dateTimePrecision) AS ?precision) 
                ?dateTime  WHERE {
    ?subject ?property ?recordCreated 
    OPTIONAL { ?recordCreated core:dateTime ?dateTime }
    OPTIONAL { ?recordCreated core:dateTimePrecision ?dateTimePrecision }

    FILTER ( bound(?dateTime) )

}
